const baseStyle = {
  paddingX: 8,
  paddingY: 4,
  borderRadius: 'radii.1',
  maxWidth: 240,
  elevation: 'shadows.3'
}

const appearances = {
  card: {
    backgroundColor: 'white'
  },
  default: {
    color: 'white',
    backgroundColor: 'palette.neutral.base'
  }
}

const sizes = {}

export default {
  baseStyle,
  appearances,
  sizes
}
