import React from 'react';
import './Banner.css';
import { Text } from "react-native";
import MaskedView from "@react-native-community/masked-view";
import LinearGradient from "react-native-linear-gradient";

export default function Banner(props) {

  return (
    <section
      className="banner"
      id="banner"
      style={{
        background: 'url("https://img.freepik.com/free-photo/group-young-business-people-working-office_158595-5210.jpg?w=740&t=st=1654770577~exp=1654771177~hmac=2898ea49b6e7217c1b1964ae464a215b772225fcc36bca3580eb82ededee884b") no-repeat',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
      }}
    >
      <div className="content">
      <b> Hey,We are Bigblue.<br/>
      We Design&develop Softwares </b>   
      <MaskedView maskElement={<Text {...props} />}>
      <LinearGradient
        colors={["red", "green"]}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 0 }}
      >
        <Text {...props} style={[props.style, { opacity: 0 }]} />
      </LinearGradient>
    </MaskedView>
      </div>
    </section>
  );
}
