import React from 'react';
import './Banner.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore,{Autoplay} from 'swiper';
export default function Banner() {
  SwiperCore.use([Autoplay]);
  return (

    <Swiper

    autoplay={{ delay: 1000, disableOnInteraction: false}}

    centeredSlides
    spaceBetween={1}

  >

       <SwiperSlide>
    <section
      className="banner"
      id="banner"
      style={{
        background: 'url("../image/banner-bg.webp") no-repeat',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
      }}
    >
      <div className="content">
        <h3>
          Orkapps
          {' '}
          <span>Grocery</span>
          {' '}
          App
        </h3>
        <p>
       welcome to Bigblue Grocery App
        </p>
        <button type="button" className="btn">
          Add card
        </button>
      </div>
    </section>
    </SwiperSlide>
    <SwiperSlide>
    <section
      className="banner"
      id="banner"
      style={{
        background: 'url("https://graphicriver.img.customer.envatousercontent.com/files/281629087/preview_13472888.jpg?auto=compress%2Cformat&q=80&fit=crop&crop=top&max-h=8000&max-w=590&s=6ab89bd1e0780c04969b4efa4a81960b") no-repeat',
        backgroundPosition: 'center',
        backgroundSize: 'cover',
      }}
    >
      <div className="content">
        <h3>
          Bigblue
          {' '}
          <span>Grocery</span>
          {' '}
          App
        </h3>
        <p>
       welcome to Bigblue Grocery App
        </p>
        <button type="button" className="btn">
          Add card
        </button>
      </div>
    </section>
    </SwiperSlide>
      </Swiper>
  );
}
